/* Reset & standardize default styles */
@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  /* Custom theme options */
  --color-accent: #35FF9A;
  --font-primary: "Bespoke Serif", serif;
  --font-secondary: "Open Sans", sans-serif;
  --bg-black: black;
  --bg-white: white;
  --bg-greenish: radial-gradient(#779db773, transparent);
  --color-text: #000411;
  --background: radial-gradient(#779db773, transparent);
  --mx: 1400px;
  /* Base values */
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2);
  --border-radius: 8px;
  --border-color: #cbcace;
}

/* Root element (use instead of `body`) */
#page {
  font-family: var(--font-secondary), system-ui, sans-serif;
  color: var(--bg-black);
  line-height: 1.5;
  font-size: 1.125rem;
  background: var(--bg-greenish);
}

/* Elements */
.section-container {
  max-width: 900px;
  margin: 0 auto;
  padding: 4rem 6rem;
  background: var(--bg-greenish);
  color: var(--color-text);

}

a.link {
  line-height: 1.25;
  font-weight: 400;
  border-bottom: 2px solid var(--color-accent, rebeccapurple);
  transform: translateY(-2px); /* move link back into place */
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: transparent;
  }
}

.heading {
  font-family: var(--font-primary);
  font-size: 2rem;
  line-height: 1.15;
  font-weight: 500;
  margin-bottom: 1.25rem;
}

.button {
  color: white;
  background: var(--color-accent, rebeccapurple);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition, 0.1s box-shadow);
  border: 0; /* reset */

  &:hover {
    box-shadow: 0 0 0 2px var(--color-accent, rebeccapurple);
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent, rebeccapurple);
  }
}

/* Content Section */
.section .content {
  /* max-width: 900px; */
  margin: 0 auto;
  padding: 4rem 2rem;

  p {
    padding: 0.25rem 0;
  }

  img {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  a.link {
  }

  h1 {
    font-family: var(--font-primary);
    font-size: 3rem;
    font-weight: 500;
    margin-bottom: 1rem;
  }

  h2 {
    font-family: var(--font-primary);
    font-size: 2rem;
    font-weight: 500;
    margin-bottom: 1rem;
  }

  h3 {
    font-family: var(--font-primary);
    font-size: 1.5rem;
    font-weight: 500;
    margin-bottom: 0.5rem;
  }

  ul {
    list-style: disc;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  ol {
    list-style: decimal;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  blockquote {
    padding: 2rem;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }
}

